import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UserStoryService } from 'src/app/services/user-story.service';
import { UserStory } from 'src/app/models/user-story.model';

@Component({
  selector: 'app-user-story-list',
  templateUrl: './user-story-list.component.html'
})
export class UserStoryListComponent implements OnInit {
  userStories: UserStory[] = [];

  constructor(
    private userStoryService: UserStoryService,
    private router: Router
  ) {}

  ngOnInit() {
    this.loadUserStories();
  }

  loadUserStories() {
    this.userStoryService.getUserStories().subscribe((data) => {
      this.userStories = data;
    });
  }

  navigateToAdd() {
    this.router.navigate(['/add-story']); // Adjust the route as per your routing configuration
  }

  navigateToEdit(storyName: string | undefined) {
    if (storyName !== undefined) {
      this.router.navigate(['/edit', storyName]);
    }
  }
  
  deleteUserStory(storyName: string | undefined) {
    if (storyName !== undefined && confirm('Are you sure you want to delete this user story?')) {
      this.userStoryService.deleteUserStoryByName(storyName).subscribe(() => {
        this.userStories = this.userStories.filter(story => story.title !== storyName);
      });
    }
  }
  
  // ... other methods ...
}
