import { Component } from '@angular/core';

@Component({
  selector: 'software-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})
export class HeaderComponent {
  signedIn: boolean = true;

  // expanding links
  programExpanded: boolean = false;
  resourcesExpanded: boolean = false;
  libraryExpanded: boolean = false;

  // getCurrentYear
  currentYear: number = new Date().getFullYear();
}
<!-- Existing header content ... -->

<!-- Left Offcanvas Menu -->
<div class="offcanvas offcanvas-start bg-dark" tabindex="-1" id="offCanvasLeft" aria-labelledby="offCanvasLeftLabel">
  <!-- Existing offcanvas header content ... -->

  <div class="offcanvas-body pt-0">
    <!-- Existing Nav Links ... -->

    <!-- Additional Links for User Stories -->
    <ul class="nav flex-column border-top pt-2">
      <!-- Existing items ... -->

      <!-- Link to View User Stories -->
      <li class="nav-item">
        <a class="nav-link" routerLink="/user-stories">
          <i class="bi bi-list-ul"></i>
          <span class="ps-1">View User Stories</span>
        </a>
      </li>

      <!-- Link to Add a New User Story -->
      <li class="nav-item">
        <a class="nav-link" routerLink="/add-story">
          <i class="bi bi-plus-square"></i>
          <span class="ps-1">Add User Story</span>
        </a>
      </li>
    </ul>

    <!-- Existing footer links ... -->
  </div>
</div>

<!-- Existing right offcanvas menu ... -->
