import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { UserStoryService } from 'src/app/services/user-story.service';
import { UserStory } from 'src/app/models/user-story.model';

@Component({
    selector: 'app-user-story-form',
    templateUrl: './user-story-form.component.html',
    styleUrls: ['./user-story-form.componen.scss'] // Correct file name
  })
  
export class UserStoryFormComponent implements OnInit {
  userStory: UserStory = new UserStory();
  editMode: boolean = false;

  constructor(
    private userStoryService: UserStoryService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit() {
    const storyId = this.route.snapshot.params['id'];
    if (storyId) {
      this.editMode = true;
      this.userStoryService.getUserStoryById(+storyId).subscribe({
        next: (data) => {
          this.userStory = data;
        },
        error: (err) => {
          // Handle error (e.g., user story not found)
          console.error(err);
          this.router.navigate(['/']); // or navigate to an error page
        }
      });
    }
  }

  saveUserStory() {
    if (this.editMode) {
      this.userStoryService.updateUserStory(this.userStory).subscribe({
        next: () => {
          this.router.navigate(['/']); // navigate to a success page or user story list
        },
        error: (err) => {
          // Handle error (e.g., display an error message)
          console.error(err);
        }
      });
    } else {
      this.userStoryService.addUserStory(this.userStory).subscribe({
        next: () => {
          this.router.navigate(['/']); // navigate to a success page or user story list
        },
        error: (err) => {
          // Handle error
          console.error(err);
        }
      });
    }
  }
}
