import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { UserStory } from '../models/user-story.model';
import { tap, catchError } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class UserStoryService {
  private apiUrl = 'http://localhost:3000/user-stories';
  userStory: UserStory = new UserStory();
  editMode: boolean = false;
  userStories: UserStory[] = []; 

  constructor(private http: HttpClient) {}

  getUserStories(): Observable<UserStory[]> {
    return this.http.get<UserStory[]>(this.apiUrl);
  }

  getUserStoryById(id: number): Observable<UserStory> {
    return this.http.get<UserStory>(`${this.apiUrl}/${id}`);
  }

  addUserStory(userStory: UserStory): Observable<UserStory> {
    return this.http.post<UserStory>(this.apiUrl, userStory).pipe(
      tap((newStory) => {
        console.log('New story added:', newStory);
        userStory.id = newStory.id; // Update the userStory object's ID with the received ID
        
        // Add the new user story to the array
        this.userStories.push(newStory);
  
        // Clear the form
        this.userStory = new UserStory();
      }),
      catchError((error) => {
        console.error('Error adding story:', error);
        throw error;
      })
    );
  }
  
  

  updateUserStory(userStory: UserStory): Observable<UserStory> {
    return this.http.put<UserStory>(`${this.apiUrl}/${userStory.id}`, userStory);
  }

  deleteUserStory(id: number): Observable<any> {
    return this.http.delete(`${this.apiUrl}/${id}`);
  }

  // ... other methods ...
}
