import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { UserStoryService } from 'src/app/services/user-story.service';
import { UserStory } from 'src/app/models/user-story.model';

@Component({
    selector: 'app-user-story-form',
    templateUrl: './user-story-form.component.html',
    styleUrls: ['./user-story-form.component.scss'],
})
export class UserStoryFormComponent implements OnInit {
    userStory: UserStory = new UserStory();
    editMode: boolean = false;
    userStories: UserStory[] = []; // Array to hold user stories

    constructor(
        private userStoryService: UserStoryService,
        private route: ActivatedRoute,
        private router: Router
    ) {}

    ngOnInit() {
        const title = this.route.snapshot.params['title']; // Change 'id' to 'title'
        if (title) {
            this.editMode = true;
    
            // Pass the title as the argument
            this.userStoryService.getUserStoryByTitle(title).subscribe((data) => {
                this.userStory = data;
            });
        }
    
        // Load existing user stories
        this.userStoryService.getUserStories().subscribe((data) => {
            this.userStories = data;
        });
    }
    
    saveUserStory() {
        if (this.editMode) {
            this.userStoryService.updateUserStoryByName(this.userStory.title, this.userStory).subscribe(() => {
                // No navigation in edit mode
            });
        } else {
            const newUserStory = new UserStory();
            newUserStory.title = this.userStory.title;
            newUserStory.description = this.userStory.description;

            this.userStoryService.addUserStory(newUserStory).subscribe((newStory) => {
                this.userStories.push(newStory);

                this.userStory = new UserStory(); // Reset the form after adding
            });
        }
    }

    editUserStory(title: string) {
        console.log('Editing story with name:', title);

        this.router.navigate(['/edit-story', title]);
    }

    deleteUserStory(title: string) {
        console.log('Deleting story with title:', title);
    
        // Check if the story with the given title exists in the userStories array
        const existingStory = this.userStories.find((story) => story.title === title);
    
        if (existingStory) {
            // Call the service to delete the user story by its title
            this.userStoryService.deleteUserStoryByName(title).subscribe(() => {
                // Remove the deleted story from the array
                this.userStories = this.userStories.filter((story) => story.title !== title);
            });
        } else {
            // Handle the case where the story was not found
            console.error('Story not found');
        }
    }
    
    
}
<form (ngSubmit)="saveUserStory()">
    <div>
        <label>Title</label>
        <input type="text" [(ngModel)]="userStory.title" name="title">
    </div>
    <div>
        <label>Description</label>
        <textarea [(ngModel)]="userStory.description" name="description"></textarea>
    </div>
    <button type="submit">Save</button>
</form>
