import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { UserStory } from '../models/user-story.model'; // Ensure the path is correct
import { tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class UserStoryService {
  private apiUrl = 'http://your-api-url.com/user-stories'; // Replace with your actual API URL

  constructor(private http: HttpClient) {}

  getUserStories(): Observable<UserStory[]> {
    return this.http.get<UserStory[]>(this.apiUrl);
  }

  getUserStoryById(id: number): Observable<UserStory> {
    return this.http.get<UserStory>(`${this.apiUrl}/${id}`);
  }

  addUserStory(userStory: UserStory): Observable<UserStory> {
    return this.http.post<UserStory>(this.apiUrl, userStory).pipe(
      tap((newStory) => {
        console.log('New story added:', newStory); // Add this line for debugging
      })
    );
  }
  

  updateUserStory(userStory: UserStory): Observable<UserStory> {
    return this.http.put<UserStory>(`${this.apiUrl}/${userStory.id}`, userStory);
  }

  deleteUserStory(id: number): Observable<any> {
    return this.http.delete(`${this.apiUrl}/${id}`);
  }

  // ... other methods ...
}
